import React, { useState } from "react";
import { Box } from "theme-ui";
import { Scrollbars } from "react-custom-scrollbars";
import Drawer from "components/drawer";
import { IoMdClose, IoMdMenu } from "react-icons/io";
import { Link } from "react-scroll";
import {
  FaFacebookF,
  FaTwitter,
  FaGithubAlt,
  FaDribbble,
} from "react-icons/fa";
import { linksData } from "./header.data";
import { mobileDrawerStyles } from "./styles";

const MobileDrawer = () => {
  const [isDrawerOpen, setDrawerOpen] = useState(false);

  const DrawerHandler = () => {
    return (
      <Box sx={mobileDrawerStyles.handler}>
        <IoMdMenu size="26px" />
      </Box>
    );
  };

  return (
    <Drawer
      width="320px"
      drawerHandler={<DrawerHandler />}
      open={isDrawerOpen}
      toggleHandler={() => setDrawerOpen(!isDrawerOpen)}
      closeButton={<IoMdClose size="24px" color="#000000" />}
      drawerStyle={mobileDrawerStyles.drawer}
      closeBtnStyle={mobileDrawerStyles.close}
    />
  );
};

export default MobileDrawer;
